import React from 'react';
import {Route} from 'react-router-dom'
import { createStructuredSelector } from 'reselect';
import {connect} from 'react-redux';
import WithSpinner from '../../components/with-spinner/with-spinner.component';
import CollectionOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';
import { fetchCollectionsStatrtAsync } from '../../redux/shop/shop.actions'
import {selectIsCollectionsFetching, selectIsCollectionsLoaded} from '../../redux/shop/shop.selectors';
const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

    componentDidMount(){

        const { fetchCollectionsStatrtAsync} = this.props;
        fetchCollectionsStatrtAsync()
    }


   render(){
       const {match, isFetchingCollections, isCollectionsLoaded } = this.props;
      
       return ( 
            <div>
                <Route 
                    exact 
                    path={`${match.path}`} 
                    render={(props) => (
                        <CollectionOverviewWithSpinner 
                        isLoading = {isFetchingCollections}
                        {...props} 
                        />)}
                />
                <Route 
                    path={`${match.path}/:collectionId`} 
                    render={(props) => (
                        <CollectionPageWithSpinner 
                        isLoading = {!isCollectionsLoaded}
                        {...props} 
                        />)}
                />    
            </div>
        )
   }
}

const mapStateToProps = createStructuredSelector({
    isFetchingCollections: selectIsCollectionsFetching,
    isCollectionsLoaded: selectIsCollectionsLoaded
})
const mapDispatchToProps = dispatch => ({
    fetchCollectionsStatrtAsync: () => dispatch(fetchCollectionsStatrtAsync())

})
export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
